import React, { useEffect, useState } from 'react';



export default function Weather() {
  const [city, setCity] = useState('');
  const [currentWeather, setCurrentWeather] = useState(null);
  const [history, setHistory] = useState([]);
  const [selectedWeather, setSelectedWeather] = useState(null);

  useEffect(() => { fetchHistory(); }, []);

  const fetchHistory = async () => {
    try {
    //  const res = await api.get('/history');
      setHistory(res.data);
    } catch (err) {
      console.error('Error loading history', err);
    }
  };

  const handleSearch = async () => {
    if (!city) return;
    try {
      const res = await api.post('/weather', { city });
      setCurrentWeather(res.data);
      setSelectedWeather(null);
      fetchHistory();
      setCity('');
    } catch (err) {
      console.error('Weather fetch error', err);
    }
  };

  return (
    <div className="weather-container">
      <h2>Weather Dashboard</h2>

      <div style={{ marginBottom: '1rem' }}>
        <input
          value={city}
          onChange={(e) => setCity(e.target.value)}
          placeholder="Enter city..."
          style={{ padding: '0.5rem', width: '70%' }}
        />
        <button onClick={handleSearch} style={{ padding: '0.5rem 1rem', marginLeft: '1rem' }}>
          Search
        </button>
      </div>

      {currentWeather && (
        <div className="weather-card">
          <h3>Current: {currentWeather.city}</h3>
          <p>Temp: {currentWeather.temperature} °C</p>
          <p>{currentWeather.description}</p>
        </div>
      )}

      {selectedWeather && (
        <div className="weather-card">
          <h3>From History: {selectedWeather.city}</h3>
          <p>Temp: {selectedWeather.temperature} °C</p>
          <p>{selectedWeather.description}</p>
          <p>{new Date(selectedWeather.timestamp).toLocaleString()}</p>
        </div>
      )}

      <h4>Recent Searches</h4>
      <ul className="history-list">
        {history.map(entry => (
          <li key={entry.id} onClick={() => setSelectedWeather(entry)}>
            {entry.city} — {new Date(entry.timestamp).toLocaleString()}
          </li>
        ))}
      </ul>
    </div>
  );
}
