import React, { useState } from "react";
import axios from "../api/axiosConfig";


const WeatherSearch = () => {
  const [formData, setFormData] = useState({ city: "" });
  const [weatherDisplay, setWeatherDisplay] = useState(null);

  const handleChange = (e) => {
    setFormData({ city: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("/weather", { city: formData.city });
      setWeatherDisplay(res.data);
      setFormData({ city: "" });
    } catch (error) {
      console.error("Weather fetch error", error);
    }
  };

  return (
    <div className="weather-container">
      <h1>WEATHER DASHBOARD</h1>

      <form onSubmit={handleSubmit}>
        <label>City:</label>
        <input
          type="text"
          name="city"
          placeholder="Enter a city"
          value={formData.city}
          onChange={handleChange}
        />
        <button type="submit">Search</button>
      </form>

      {weatherDisplay && (
        <div className="weather-card">
          <h2>Current Weather</h2>
          <p><strong>City:</strong> {weatherDisplay.city}</p>
          <p><strong>Temperature:</strong> {weatherDisplay.temperature}Â°C</p>
          <p><strong>Description:</strong> {weatherDisplay.description}</p>
        </div>
      )}
    </div>
  );
};

export default WeatherSearch;
